          align-items: center;
  -webkit-transition: opacity 0.25s ease !important;
  -o-transition: opacity 0.25s ease !important;
  transition: opacity 0.25s ease !important;
  display: none;
}

[dir="rtl"] .slick-slide {
  float: right;
}

.slick-slide img {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
}

.slick-slide.slick-loading img {
  display: none;
}

.slick-slide.dragging img {
  pointer-events: none;
}

.slick-slide:focus {
  outline: none;
}

.slick-initialized .slick-slide {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
}

.slick-loading .slick-slide {
  visibility: hidden;
}

.slick-vertical .slick-slide {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  height: auto;
  border: 1px solid transparent;
}

.slick-arrow.slick-hidden {
  display: none;
}

.slick-disabled {
  opacity: 0.5;
}

.slick-dots {
  height: 2.5rem;
  line-height: 2.5rem;
  width: 100%;
  list-style: none;
  text-align: center;
}

.slick-dots li {
