.u-overlay::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(85, 75, 71, 0.4);
  z-index: 0;
  pointer-events: none;
}

.fluid-width-video-wrapper {
  padding-top: 56.25% !important;
}

/**
 * Completely remove from the flow and screen readers.
 */

.is-hidden {
  visibility: hidden;
}

.hide {
  display: none;
}

/**
 * Completely remove from the flow but leave available to screen readers.
 */

.is-vishidden,
.screen-reader-text,
.sr-only {
  position: absolute !important;
  overflow: hidden;
  width: 1px;
  height: 1px;
  padding: 0;
  border: 0;
  clip: rect(1px, 1px, 1px, 1px);
}

/**
 * Hide elements only present and necessary for js enabled browsers.
 */

.no-js .no-js-hide {
  display: none;
}

/**
 * Round Element
 */

.u-round {
  overflow: hidden;
  border-radius: 100%;
}

/**
 * Misc
 */

.u-overflow--hidden {
  overflow: hidden;
}

.u-width--100p {
  width: 100%;
}

/**
 * Alignment
