:root {
  --font-size-xs: 12px;
  --font-size-s: 14px;
  --font-size-m: 16px;
  --font-size-l: 20px;
  --font-size-xl: 24px;
  --font-size-xxl: 100px; }

@media screen and (min-width: 500px) {
  :root {
    --font-size-xs: 14px;
    --font-size-s: 16px;
    --font-size-m: 18px;
    --font-size-l: 22px;
    --font-size-xl: 30px;
    --font-size-xxl: 125px; } }

@media screen and (min-width: 1100px) {
  :root {
    --font-size-xs: 15px;
    --font-size-s: 19px;
    --font-size-m: 20px;
    --font-size-l: 24px;
    --font-size-xl: 36px;
    --font-size-xxl: 150px; } }

/* ------------------------------------ *\
