/* ------------------------------------ *\
    $RESET
\* ------------------------------------ */
/* Border-Box http:/paulirish.com/2012/box-sizing-border-box-ftw/ */
* {
  -moz-box-sizing: border-box;
  -webkit-box-sizing: border-box;
  box-sizing: border-box; }

body {
  margin: 0;
  padding: 0; }

blockquote,
body,
div,
figure,
footer,
form,
h1,
h2,
h3,
h4,
h5,
h6,
header,
html,
iframe,
label,
legend,
li,
nav,
object,
ol,
p,
section,
table,
ul {
  margin: 0;
  padding: 0; }

article,
figure,
footer,
header,
hgroup,
nav,
section {
  display: block; }

address {
  font-style: normal; }

/* ------------------------------------ *\
    $BASE
\* ------------------------------------ */
/* ------------------------------------ *\
    $FONTS
\* ------------------------------------ */
@font-face {
  font-family: 'signature_collection_alt';
  src: url("../fonts/signature-collection-alt-webfont.woff2") format("woff2"), url("../fonts/signature-collection-alt-webfont.woff") format("woff");
  font-weight: normal;
  font-style: normal; }

@font-face {
  font-family: 'silver_south_serif';
  src: url("../fonts/silver-south-serif-webfont.woff2") format("woff2"), url("../fonts/silver-south-serif-webfont.woff") format("woff");
  font-weight: normal;
  font-style: normal; }

@font-face {
  font-family: 'nexa_book';
  src: url("../fonts/nexa-book-webfont.woff2") format("woff2"), url("../fonts/nexa-book-webfont.woff") format("woff");
  font-weight: normal;
  font-style: normal; }

@font-face {
  font-family: 'nexa_book_italic';
  src: url("../fonts/nexa-book-italic-webfont.woff2") format("woff2"), url("../fonts/nexa-book-italic-webfont.woff") format("woff");
  font-weight: normal;
  font-style: normal; }

@font-face {
  font-family: 'nexa_bold';
  src: url("../fonts/nexa-bold-webfont.woff2") format("woff2"), url("../fonts/nexa-bold-webfont.woff") format("woff");
  font-weight: normal;
  font-style: normal; }

@font-face {
  font-family: 'nexa_heavy';
  src: url("../fonts/nexa-heavy-webfont.woff2") format("woff2"), url("../fonts/nexa-heavy-webfont.woff") format("woff");
  font-weight: normal;
  font-style: normal; }

/* ------------------------------------ *\
    $FORMS
\* ------------------------------------ */
form {
  font-family: "nexa_book", "Arial", "Helvetica", sans-serif;
  font-family: "nexa_book", "Arial", "Helvetica", sans-serif;
  font-size: 1rem;
  line-height: 1.5; }

form ol,
form ul {
  list-style: none;
  margin-left: 0; }

legend {
  margin-bottom: 0.375rem;
  font-weight: bold; }

fieldset {
  border: 0;
  padding: 0;
  margin: 0;
  min-width: 0; }

button,
input,
select,
textarea {
  font-family: inherit;
  font-size: 100%; }

input,
select,
textarea {
  width: 100%;
  border: 1px solid #e0d8d6;
  padding: 20px;
  -webkit-appearance: none;
  border-radius: 0.1875rem;
  outline: 0; }

input[type="checkbox"],
input[type="radio"] {
  width: auto;
  margin-right: 0.3em; }

input[type="search"] {
  -webkit-appearance: none;
  border-radius: 0; }

input[type="search"]::-webkit-search-cancel-button,
input[type="search"]::-webkit-search-decoration {
  -webkit-appearance: none; }

::placeholder {
  color: #adadad; }

/**
 * Validation
 */
.has-error {
  border-color: #f00 !important; }

.is-valid {
  border-color: #089e00 !important; }

.c-form label {
  margin-bottom: 10px;
  display: block; }

.c-form__fields {
  display: flex;
  flex-wrap: wrap;
  margin-left: calc($space/2 * -1);
  margin-right: calc($space/2 * -1); }
  .c-form__fields-item {
    padding: 0 10px;
    margin-bottom: 20px;
    width: 100%; }
    .c-form__fields-item--half {
      width: 50%; }
    .c-form__fields-item--qaurter {
      width: 25%; }

/* ------------------------------------ *\
    $HEADINGS
\* ------------------------------------ */
h1,
.o-heading--xxl {
  font-family: "signature_collection_alt";
  font-size: var(--font-size-xxl);
  line-height: 1;
  font-weight: normal; }

h2,
.o-heading--xl {
  font-family: "silver_south_serif", "Times New Roman", serif;
  font-size: var(--font-size-xl);
  line-height: 1.15;
  letter-spacing: 0.1em; }

h3,
.o-heading--l {
  font-family: "silver_south_serif", "Times New Roman", serif;
  font-size: var(--font-size-l);
  line-height: 1.25;
  letter-spacing: 0.1em; }

h4,
.o-heading--m {
  font-family: "silver_south_serif", "Times New Roman", serif;
  font-size: var(--font-size-m);
  line-height: 1.35;
  letter-spacing: 0.05em; }

h5,
.o-heading--s {
  font-family: "nexa_book", "Arial", "Helvetica", sans-serif;
  font-size: var(--font-size-s);
  letter-spacing: 0.1em; }

h6,
.o-heading--xs {
  font-family: "nexa_book", "Arial", "Helvetica", sans-serif;
  font-size: var(--font-size-xs);
  letter-spacing: 0.1em; }

/* ------------------------------------ *\
    $LINKS
\* ------------------------------------ */
a {
  text-decoration: none;
  color: #554b47;
  transition: all 0.25s ease-in-out;
  display: inline-block; }

.u-link--underline {
  border-bottom: 1px solid #e0d8d6; }
  .u-link--underline:hover {
    border-color: #cfb6b5; }

/* ------------------------------------ *\
    $LISTS
\* ------------------------------------ */
ol,
ul {
  margin: 0;
  padding: 0;
  list-style: none; }

/**
 * Definition Lists
 */
dl {
  overflow: hidden;
  margin: 0 0 20px; }

dt {
  font-weight: bold; }

dd {
  margin-left: 0; }

.o-list--numbered {
  counter-reset: item; }
  .o-list--numbered li {
    display: block; }
