/**
 * Convert px to rem.
 *
 * @param int $size
 *   Size in px unit.
 * @return string
 *   Returns px unit converted to rem.
 */
@function rem($size) {
  $remSize: $size / $rembase;

  @return #{$remSize}rem;
}

