/**
 * Grid & Baseline Setup
 */
$ff-fontpx: 16; // Font size (px) baseline applied to <body> and converted to %.
$defaultpx: 16; // Browser default px used for media queries
$rembase: 16; // 16px = 1.00rem
$max-width-px: 1260;
$max-width: rem($max-width-px) !default;

/**
  * Theme Colors
  */
$c-green: #d8ded7;
$c-pink: #cfb6b5;
$c-brown: #554b47;
$c-tan: #e0d8d6;
$c-off-white: #f6f6f6;
$c-primary: $c-green;
$c-secondary: $c-pink;
$c-tertiary: $c-off-white;

/**
 * Neutral Colors
 */
$c-black: $c-brown;
$c-gray: #adadad;
$c-gray--light: #f3f3f3;
$c-white: #fff;

/**
 * Default Colors
 */
$c-error: #f00;
$c-valid: #089e00;
$c-warning: #fff664;
$c-information: #000db5;
$c-overlay: rgba($c-black, 0.8);

/**
 * Style Colors
 */
$c-body-color: $c-brown;
$c-link-color: $c-brown;
$c-border-color: $c-tan;

/**
 * Typography
 */
$ff-font--sans: 'nexa_book', 'Arial', 'Helvetica', sans-serif;
$ff-font--serif: 'silver_south_serif', 'Times New Roman', serif;
$ff-font--script: 'signature_collection_alt';
$ff-font--monospace: Menlo, Monaco, 'Courier New', 'Courier', monospace;
$ff-font: $ff-font--sans;
$ff-font--primary: $ff-font--serif;
$ff-font--secondary: $ff-font--script;

/**
 * Icons
 */
$icon-xsmall: rem(15);
$icon-small: rem(20);
$icon-medium: rem(30);
$icon-large: rem(40);
$icon-xlarge: rem(50);

/**
 * Common Breakpoints
 */
$xsmall: 350px;
$small: 500px;
$medium: 700px;
$large: 900px;
$xlarge: 1100px;
$xxlarge: 1300px;
$xxxlarge: 1500px;

$breakpoints: (
  'xsmall': $xsmall,
  'small': $small,
  'medium': $medium,
  'large': $large,
  'xlarge': $xlarge,
  'xxlarge': $xxlarge,
  'xxxlarge': $xxxlarge
);

/**
 * Border Styles
 */
$border-med: 6px;
$border-thick: 8px;
$border-opacity: 1px solid rgba($c-white, 0.2);
$border-style: 1px solid $c-border-color;
$border-style-thick: 3px solid $c-border-color;

/**
 * Default Spacing/Padding
 */
$space-mobile: 20px;
$space: 20px;

/**
 * Native Custom Properties
 */
:root {
  --font-size-xs: 12px;
  --font-size-s: 14px;
  --font-size-m: 16px;
  --font-size-l: 20px;
  --font-size-xl: 24px;
  --font-size-xxl: 100px;
}

// Small Breakpoint
@media screen and (min-width: 500px) {
  :root {
    --font-size-xs: 14px;
    --font-size-s: 16px;
    --font-size-m: 18px;
    --font-size-l: 22px;
    --font-size-xl: 30px;
    --font-size-xxl: 125px;
  }
}

// Large Breakpoint
@media screen and (min-width: 1100px) {
  :root {
    --font-size-xs: 15px;
    --font-size-s: 19px;
    --font-size-m: 20px;
    --font-size-l: 24px;
    --font-size-xl: 36px;
    --font-size-xxl: 150px;
  }
}
","/* ------------------------------------ *\
    $MEDIA QUERY TESTS
\* ------------------------------------ */

@if $tests == true {
  body {
    &::before {
      display: block;
      position: fixed;
      z-index: 100000;
      background: black;
      bottom: 0;
      right: 0;
      padding: 0.5em 1em;
      content: 'No Media Query';
      color: transparentize(#fff, 0.25);
      border-top-left-radius: 10px;
      font-size: (12/16)+em;

      @media print {
        display: none;
      }
    }

    &::after {
      display: block;
      position: fixed;
      height: 5px;
      bottom: 0;
      left: 0;
      right: 0;
      z-index: (100000);
      content: '';
      background: black;

      @media print {
        display: none;
      }
    }

    @include media('>xsmall') {
      &::before {
        content: 'xsmall: 350px';
      }

      &::after,
      &::before {
        background: dodgerblue;
      }
    }

    @include media('>small') {
      &::before {
        content: 'small: 500px';
      }

      &::after,
      &::before {
        background: darkseagreen;
      }
    }

    @include media('>medium') {
      &::before {
        content: 'medium: 700px';
      }

      &::after,
      &::before {
        background: lightcoral;
      }
    }

    @include media('>large') {
      &::before {
        content: 'large: 900px';
      }

      &::after,
      &::before {
        background: mediumvioletred;
      }
    }

    @include media('>xlarge') {
      &::before {
        content: 'xlarge: 1100px';
      }

      &::after,
      &::before {
        background: hotpink;
      }
    }

    @include media('>xxlarge') {
      &::before {
        content: 'xxlarge: 1300px';
      }

      &::after,
      &::before {
        background: orangered;
      }
    }

    @include media('>xxxlarge') {
      &::before {
        content: 'xxxlarge: 1400px';
      }

      &::after,
      &::before {
        background: dodgerblue;
      }
    }
  }
}
