  }

  &-item {
    position: relative;
  }

  &--2up {
    align-items: center;

    @include media('>medium') {
      grid-template-columns: repeat(2, 1fr);
    }

    &--flex {
      display: flex;
      flex-wrap: wrap;
      margin: 0 calc($space * -1);

      @include media('>xxlarge') {
        margin: 0 calc($space*1.5 * -1);
      }

