        width: 100%;
        padding-left: $space;
        padding-right: $space;
        margin-top: $space*2;

        @include media('>small') {
          width: 50%;
        }

        @include media('>xxlarge') {
          padding-left: $space*1.5;
          padding-right: $space*1.5;
          margin-top: $space*3;
        }
      }
    }
  }

  &--3up {
    @include media('>medium') {
      grid-template-columns: repeat(3, 1fr);
    }
  }

  &--4up {
