","/* ------------------------------------ *\
    $SPACING
\* ------------------------------------ */

$sizes: (
  '': $space,
  --quarter: $space/4,
  --half: $space/2,
  --and-half: $space*1.5,
  --double: $space*2,
  --triple: $space*3,
  --quad: $space*4,
  --zero: 0rem
);

$sides: (
  '':'',
  --top: '-top',
  --bottom: '-bottom',
  --left: '-left',
  --right: '-right'
);

@each $size_key, $size_value in $sizes {
  .u-spacing#{$size_key} {
    & > * + * {
      margin-top: #{$size_value};
    }
  }

  @each $side_key, $side_value in $sides {
    .u-padding#{$size_key}#{$side_key} {
      padding#{$side_value}: #{$size_value};
    }

    .u-space#{$size_key}#{$side_key} {
      margin#{$side_value}: #{$size_value};
    }
  }
}
","/* Slider */
.slick-slider {
  position: relative;
  display: flex;
  box-sizing: border-box;
  -webkit-touch-callout: none;
  -webkit-user-select: none;
  -khtml-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  -ms-touch-action: pan-y;
  touch-action: pan-y;
  -webkit-tap-highlight-color: transparent;
}

.slick-list {
  position: relative;
  overflow: hidden;
  display: block;
  margin: 0;
  padding: 0;

  &:focus {
    outline: none;
  }

  &.dragging {
    cursor: pointer;
    cursor: hand;
  }
}

.slick-track {
  position: relative;
  left: 0;
  top: 0;
  display: block;
  height: 100%;

  &::before,
  &::after {
    content: "";
    display: table;
  }

  &::after {
    clear: both;
  }

  .slick-loading & {
    visibility: hidden;
  }
}

.slick-slider .slick-track,
.slick-slider .slick-list {
  -webkit-transform: translate3d(0, 0, 0);
  -moz-transform: translate3d(0, 0, 0);
  -ms-transform: translate3d(0, 0, 0);
  -o-transform: translate3d(0, 0, 0);
  transform: translate3d(0, 0, 0);
}

.slick-slide {
  float: left;
  height: 100%;
  min-height: 1px;
  justify-content: center;
  align-items: center;
  transition: opacity 0.25s ease !important;

  [dir="rtl"] & {
    float: right;
  }

  img {
    display: flex;
  }

  &.slick-loading img {
    display: none;
  }

  display: none;

  &.dragging img {
    pointer-events: none;
  }

  &:focus {
    outline: none;
  }

  .slick-initialized & {
    display: flex;
  }

  .slick-loading & {
    visibility: hidden;
  }

  .slick-vertical & {
    display: flex;
    height: auto;
    border: 1px solid transparent;
  }
}

.slick-arrow.slick-hidden {
  display: none;
}

.slick-disabled {
  opacity: 0.5;
}

.slick-dots {
  height: rem(40);
  line-height: rem(40);
  width: 100%;
  list-style: none;
  text-align: center;

